import React, { Component } from "react";

export default class FormValidation extends Component {
    constructor(props){
        super(props);
        this.state = {
            manv: "",
            tennv: "",
            email: "",
        }

    }
  render() {
    return (
      <div className="container">
        <h3>* Form Validation</h3>
        <form>
          <div className="form-group">
            <label>Mã Nhân Viên</label>
            <input type="text" className="form-control" name="manv" onChange={this.handleOnChange} />
          </div>
          <div className="form-group">
            <label>Tên Nhân Viên</label>
            <input type="text" className="form-control" name="tennv" onChange={this.handleOnChange} />
          </div>
          <div className="form-group">
            <label>Email</label>
            <input type="email" className="form-control" name="email" onChange={this.handleOnChange} />
          </div>
          <button type="submit" className="btn btn-success">
            Submit
          </button>
        </form>
      </div>
    );
  }
}
